INSERT INTO `email_template_lang` (`id`,`email_template_id`,`language_id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`body`,`subject`) VALUES (38,40,1,NULL,NULL,NULL,NULL,NULL,NULL,'<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n         \n <html xmlns=\"http://www.w3.org/1999/xhtml\">\n <head>\n     <title>Email</title>\n </head>\n <body>\n <center>\n	\n     <table align=\"center\">\n		<tr>\n			<td><p>Hi $$firstname$$,</p></td>\n		</tr>\n         <tr>\n             <th style=\"padding:0px;margin:0px;\">\n         \n                <table  style=\"font-family:helvetica,arial,verdana,san-serif;font-weight:normal;width:800px; height=337px;text-align:center;padding:0px;\">\n                <tr bgcolor=\"#eeeeee\" style=\"width:800px;padding:0px;height:337px;\">\n                <td style=\"width:800px;padding:0px;height:337px;\">\n                <table style=\"text-align:center;width:100%\">\n                <tr>\n                     <td style=\"padding:0px;\">\n                     <table style=\"margin-top:56px;width:100%\">\n 		<tr>\n 		<td style=\"text-align:center;padding:0px;font-size:33px;height:80px;width:800px;font-family:helvetica,arial,verdana,san-serif;font-weight:normal;color:#000000\">\n 					<br>Welcome to Mapworks.\n 		</td>\n 		</tr>\n 		</table>\n                     </td>\n                </tr>\n                <tr style=\"margin:0px;padding:0px;\">\n 		<td style=\"text-align:center;font-family:helvetica,arial,verdana,san-serif;font-weight:normal;color:#333333;font-size: 16px;height:16px;padding-top:8px;\">\n         			Use the link below to create your password and start using Mapworks.\n         \n 		</td></tr>\n            <tr style=\"margin:0px;padding:0px;\"><td style=\"margin:0px;padding:0px;\">\n         \n <table align=\"center\">\n   <tr style=\"margin:0px;padding:0px;\">\n     <th style=\"margin:0px;padding:0px;\">\n            <table cellpadding=\"36\" style=\"width:100%\">\n         <tr>\n 		<td align=\"center\" style=\"text-align:center;color:#000000;font-weight:normal;font-size: 20px;\">\n 		          <table style=\"border-radius:2px;width:175px;font-size:20px;font-family:helvetica,arial,verdana,san-serif;font-weight:normal;text-align:center;display: block;margin: 0 auto;padding:0px 0px\">\n 		<tr>\n         <td style=\"background-color:#4673a7; height:58px;border-radius:2px;line-height:21px;text-decoration:none ;vertical-align:middle;\">\n         <a href=\"$$activation_token$$\" style=\"outline-offset:19px !important;background-color: #4673a7; color: #ffffff;display: block;text-decoration: none;width:175px \"target=\"_blank\"><span style=\"text-decoration: none !important;\">Sign In Now</span></a>\n         </td></tr>\n         <tr valign=\"top\" style=\"height:33px;\">\n         <td style=\"margin-left:auto; margin-right:auto;width:100%;font-size: 14px;height:14px;padding-bottom:7px;font-family:helvetica,arial,verdana,san-serif;font-weight:medium;color:#333333;link:#1e73d5;padding-top:8px;\">\n 				<span>Use this link to <a target=\"_blank\" style=\"link:#1e73d5;\" href=\"$$activation_token$$\">sign in.</a></span>\n         \n         </td></tr>\n         \n         </table>\n 		</td></tr>\n         \n         </table>\n         </th>\n         \n   </tr>\n         \n </table>\n        </td></tr>\n </table>\n                </td>\n                </tr>\n                <tr valign=\"top\">\n <td >\n <table>\n <tr>\n <td valign=\"top\" align=\"center\">\n <div style=\"text-align:left;margin-left:30px;font-family:helvetica,arial,verdana,san-serif;font-weight:normal;\n 			margin-right:18px;font-size: 13px;color: #333333;margin-top:30px;link:#1e73d5;font-weight:normal;\" >\n 				If you believe that you received this email in error or if you have any questions, please contact Mapworks support at <a href=\"mailto:$$Support_Helpdesk_Email_Address$$\" style=\"link:#1e73d5;\">support@mapworks.com</a>.\n 				\n 				<div style=\"text-align:left;font-weight:bold;font-size: 14px;color:#333333\" >\n 					<p>Thank you from the Skyfactor Mapworks team.</br></p>\n					<p><img src=\"https://synapse-uat.mnv-tech.com/images/Skyfactor-Mapworks-login.png\" alt=\"Skyfator Mapworks Logo\"/><br/></p>\n					<p>This email is an auto-generated message. Replies to automated messages are not monitored.</p>\n 				</div>\n  </div>\n </td>\n </tr>\n </table>\n </td>\n </tr>\n                </table>\n         \n             </th>\n         \n         </tr>\n         \n     </table>\n     </center>\n </body>\n </html>','Welcome to Mapworks');
INSERT INTO `email_template_lang` (`id`,`email_template_id`,`language_id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`body`,`subject`) VALUES (39,36,1,NULL,NULL,NULL,NULL,NULL,NULL,'<html>\n    <head>\n    <style> body {\n        background: none repeat scroll 0 0# f4f4f4;\n\n    }\ntable {\n    padding: 21 px;\n    width: 799 px;\n\n\n    font - family: helvetica,\n    arial,\n    verdana,\n    san - serif;\n    font - size: 13 px;\n    color: #333;\n }\n 	</style>\n </head><body><table cellpadding= \"10\" style = \"background:#eeeeee;\" cellspacing = \"0\"> \n <tbody> \n <tr style = \"background:#fff;border-collapse:collapse;\"> <td> Hi $$firstname$$, </td></tr> <tr style = \"background:#fff;border-collapse:collapse;\"> <td> \n Your Mapworks accounts have been merged, and this account address has been deactivated. You will receive an email notification at your main account email address with instructions to reset your password.</td> </tr>\n\n								<tr>\n 								  <td style=\"font-family: helvetica neue, helvetica, arial, verdana, san-serif; font-size: 14px; color: #262727; line-height:18px;\" align=\"left\" valign=\"top\">\n 										If you believe that you received this email in error or if you have any questions, please contact Mapworks support at <a href=\"mailto:$$Support_Helpdesk_Email_Address$$\" class=\"external-link\" rel=\"nofollow\">$$Support_Helpdesk_Email_Address$$</a>\n 								  </td>\n 								</tr>\n \n <tr style=\"background:#fff;border-collapse:collapse;\"><td></td></tr>\n <tr style=\"background:#fff;border-collapse:collapse;\"><td>Thank you from the Skyfactor Mapworks team.<br/> \n </td></tr> \n \n <tr style = \"background:#fff;border-collapse:collapse;\">  <td> \n <img src = \"$$Skyfactor_Mapworks_logo$$\" alt = \"Skyfactor Mapworks logo\" title = \"Skyfactor Mapworks logo\" /> \n </td></tr> \n \n <tr style = \"background:#fff;border-collapse:collapse;\"> <td> This email is an auto-generated message.Replies to automated messages are not monitored. </td></tr> </tbody>\n      </table> </body></html>','Deactivate Email');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (1,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_Interactions_count_Groupby_Teams','E',1,'select tm.teams_id, t.team_name, count(al.id) as numbers, \'interaction\' as activity from Teams t, team_members tm, activity_log al where tm.teams_id = t.id and tm.organization_id = t.organization_id and al.organization_id = tm.organization_id and al.person_id_faculty = tm.person_id and al.activity_type in (\'R\',\'C\',\'N\',\'A\') and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and tm.teams_id in (SELECT teams_id FROM team_members where is_team_leader = 1 and person_id = \'$$loggedUserId$$\'  and deleted_at IS NULL) and tm.organization_id = \'$$organizationId$$\' and t.deleted_at IS NULL and tm.deleted_at IS NULL and al.deleted_at IS NULL group by t.team_name');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (2,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_Open_Referrals_count_Groupby_Teams','E',1,'select tm.teams_id, t.team_name, count(al.id) as numbers, \'openreferrals\' as activity from Teams t, team_members tm, activity_log al, referrals r where tm.teams_id = t.id and tm.organization_id = t.organization_id and al.organization_id = tm.organization_id and al.person_id_faculty = tm.person_id and al.activity_type = \'R\' and r.id = al.referrals_id and r.status = \'O\' and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and tm.teams_id in (SELECT teams_id FROM team_members where is_team_leader = 1 and person_id = \'$$loggedUserId$$\' and deleted_at IS NULL) and tm.organization_id = \'$$organizationId$$\' and t.deleted_at IS NULL and tm.deleted_at IS NULL and al.deleted_at IS NULL group by t.team_name');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (3,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_Logins_Count_Groupby_Teams','E',1,'select tm.teams_id, t.team_name, count(al.id) as numbers, \'logins\' as activity from Teams t, team_members tm, activity_log al where tm.teams_id = t.id and tm.organization_id = t.organization_id and al.organization_id = tm.organization_id and al.person_id_faculty = tm.person_id and al.activity_type = \'L\' and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and tm.teams_id in (SELECT teams_id FROM team_members where is_team_leader = 1 and person_id = \'$$loggedUserId$$\' and deleted_at IS NULL) and tm.organization_id = \'$$organizationId$$\' and t.deleted_at IS NULL and tm.deleted_at IS NULL and al.deleted_at IS NULL group by t.team_name');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (4,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_List_with_OpenReferrals_and_otherActivities','E',1,'(select \'openreferrals\' as activity,al.activity_date as activity_date,al.person_id_faculty as team_member_id,pa.firstname as team_member_firstname,pa.lastname as team_member_lastname, pa.username as primary_email,al.person_id_student as student_id,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.firstname ELSE \'\' END) as student_firstname,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.lastname ELSE \'\' END) as student_lastname,\'O\' as activity_type, al.referrals_id, al.appointments_id, al.note_id, al.contacts_id, \'\' as reason_id, al.reason as reason_text,r.status,gs.person_id from activity_log al left join referrals r on r.id = al.referrals_id left outer join org_group_students gs on (al.person_id_student = gs.person_id and gs.org_group_id in (select distinct org_group_id from org_group_faculty where person_id in ($$personId$$) and deleted_at IS NULL )) left join person pa on pa.id = al.person_id_faculty left join person p on p.id = al.person_id_student where al.activity_type in (\'R\') and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and al.person_id_faculty in ($$teamMemberId$$) and al.deleted_at IS NULL and al.organization_id = \'$$organizationId$$\' group by al.id) union (select \'interactions\' as activity,al.activity_date as activity_date,al.person_id_faculty as team_member_id, pa.firstname as team_member_firstname,pa.lastname as team_member_lastname, pa.username as primary_email,al.person_id_student as student_id,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.firstname ELSE \'\' END) as student_firstname,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.lastname ELSE \'\' END) as student_lastname, al.activity_type as activity_type, al.referrals_id, al.appointments_id, al.note_id, al.contacts_id, \'\' as reason_id, al.reason as reason_text,\'\' as status,gs.person_id from activity_log al left outer join org_group_students gs on (al.person_id_student = gs.person_id and gs.org_group_id in (select distinct org_group_id from org_group_faculty where person_id in ($$personId$$) and deleted_at IS NULL )) left join person pa on pa.id = al.person_id_faculty left join person p on p.id = al.person_id_student where al.activity_type in (\'A\',\'C\',\'N\',\'L\') and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and al.person_id_faculty in ($$teamMemberId$$) and al.deleted_at IS NULL and al.organization_id = \'$$organizationId$$\' group by al.id) order by activity_date');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (5,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_List_without_Referrals','E',1,'select \'interactions\' as activity,al.activity_date as activity_date,al.person_id_faculty as team_member_id,pa.firstname as team_member_firstname,pa.lastname as team_member_lastname, pa.username as primary_email,al.person_id_student as student_id, (CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.firstname ELSE \'\' END) as student_firstname,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.lastname ELSE \'\' END) as student_lastname,al.activity_type as activity_type, al.referrals_id, al.appointments_id, al.note_id, al.contacts_id, \'\' as reason_id, al.reason as reason_text,r.status,gs.person_id from activity_log al left join referrals r on r.id = al.referrals_id left outer join org_group_students gs on (al.person_id_student = gs.person_id and gs.org_group_id in (select distinct org_group_id from org_group_faculty where person_id in ($$personId$$) and deleted_at IS NULL )) left join person pa on pa.id = al.person_id_faculty left join person p on p.id = al.person_id_student where al.activity_type in (\'A\',\'C\',\'N\',\'R\') and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and al.person_id_faculty in ($$teamMemberId$$) and al.deleted_at IS NULL and al.organization_id = \'$$organizationId$$\'  group by al.id order by activity_date');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (6,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_List_with_only_Logins','E',1,'select \'logins\' as activity,al.activity_date as activity_date,al.person_id_faculty as team_member_id,pa.firstname as team_member_firstname,pa.lastname as team_member_lastname, ci.primary_email, \'\' as student_id, \'\' as student_firstname,\'\' as student_lastname,al.activity_type as activity_type, al.referrals_id, al.appointments_id, al.note_id, al.contacts_id, \'\' as activity_id, \'\' as reason_id, \'-\' as reason_text,\'\' as status from activity_log al left join person pa on pa.id = al.person_id_faculty left join person_contact_info pci on pci.person_id = al.person_id_faculty left join contact_info ci on ci.id = pci.contact_id where al.activity_type in (\'L\') and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and al.person_id_faculty in ($$teamMemberId$$) and al.deleted_at IS NULL and al.organization_id = \'$$organizationId$$\' order by activity_date');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (7,NULL,NULL,NULL,NULL,NULL,NULL,'My_Team_List_with_only_OpenReferrals','E',1,'select \'openreferrals\' as activity,al.activity_date as activity_date,al.person_id_faculty as team_member_id,pa.firstname as team_member_firstname,pa.lastname as team_member_lastname, pa.username as primary_email,al.person_id_student as student_id, (CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.firstname ELSE \'\' END) as student_firstname,(CASE WHEN gs.person_id IS NOT NULL and gs.deleted_at IS NULL then p.lastname ELSE \'\' END) as student_lastname,\'O\' as activity_type, al.referrals_id, al.appointments_id, al.note_id, al.contacts_id, \'\' as reason_id, al.reason as reason_text,r.status,gs.person_id from activity_log al left join referrals r on r.id = al.referrals_id left outer join org_group_students gs on (al.person_id_student = gs.person_id and gs.org_group_id in (select distinct org_group_id from org_group_faculty where person_id in ($$personId$$) and deleted_at IS NULL )) left join person pa on pa.id = al.person_id_faculty left join person p on p.id = al.person_id_student where al.activity_type in (\'R\') and r.status = \'O\' and al.activity_date between \'$$fromDate$$\' and \'$$toDate$$\' and al.person_id_faculty in ($$teamMemberId$$) and al.deleted_at IS NULL and al.organization_id = \'$$organizationId$$\' group by al.id order by activity_date');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (14,NULL,NULL,NULL,NULL,NULL,NULL,'My_High_priority_students_Count','E',1,'select count(per.id) as highCount from person per where per.id in \n(select distinct person_id from org_group_students where org_group_id in (select org_group_id from org_group_faculty where person_id = $$personId$$ and deleted_at is null and org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg = 1 and deleted_at is null)) and deleted_at is null union select distinct person_id from org_course_student where org_courses_id in (select org_courses_id from org_course_faculty where person_id = $$personId$$ and deleted_at is null and org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg = 1 and deleted_at is null) and org_courses_id in (select id from org_courses where deleted_at is null and org_academic_terms_id in (select id from org_academic_terms where deleted_at is null and end_date > now()))) and deleted_at is null) and per.last_contact_date < per.risk_update_date and per.risk_level in ($$risklevel$$) and per.deleted_at is null');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (15,NULL,NULL,NULL,NULL,NULL,NULL,'My_Total_Students_Count_Groupby_Risk','E',1,'select p.risk_level, count(p.id) as totalStudentsHighPriority, rml.risk_text, rml.image_name, rml.color_hex from person p, risk_level rml where p.id in (select distinct person_id from org_group_students where org_group_id in (select org_group_id from org_group_faculty where person_id = $$personId$$ and deleted_at is null and org_permissionset_id in (select id from org_permissionset where risk_indicator = 1 and deleted_at is null)) and deleted_at is null union select distinct person_id from org_course_student where org_courses_id in (select org_courses_id from org_course_faculty where person_id = $$personId$$ and deleted_at is null and org_permissionset_id in (select id from org_permissionset where risk_indicator = 1 and deleted_at is null) and org_courses_id in (select id from org_courses where deleted_at is null and org_academic_terms_id in (select id from org_academic_terms where deleted_at is null and end_date > now()))) and deleted_at is null) and rml.id = p.risk_level and p.deleted_at is null group by p.risk_level');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (16,NULL,NULL,NULL,NULL,NULL,NULL,'My_High_priority_students_List','E',1,'select p.id,p.firstname, p.lastname,p.risk_level,il.image_name as intent_imagename,il.text as intent_text,rl.image_name as risk_imagename,rl.risk_text,p.intent_to_leave as intent_leave, lc.cnt as login_cnt, p.cohert, p.last_activity,ps.status, il.color_hex as intent_color, rl.color_hex as risk_color, (CASE when (( (select distinct(ogs.person_id) from org_group_students ogs, org_group_faculty ogf, person ip where ogs.org_group_id=ogf.org_group_id and ogf.person_id=($$personId$$) and ogs.person_id=p.id and ogs.person_id = ip.id and ogf.org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg=1 and risk_indicator= 1 and deleted_at is null) order by ip.risk_level desc ,ip.lastname,ip.firstname) = p.id) or ((select distinct(ocs.person_id) from org_course_student ocs, org_course_faculty ocf, person ip where ocs.org_courses_id=ocf.org_courses_id and ocf.person_id=($$personId$$) and ocs.person_id=p.id and ocs.person_id = ip.id and ocf.org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg=1 and risk_indicator= 1 and deleted_at is null) and ocf.org_courses_id in (select id from org_courses where deleted_at is null and org_academic_terms_id in (select id from org_academic_terms where deleted_at is null and end_date > now())) order by ip.risk_level desc ,ip.lastname,ip.firstname )= p.id)) then \"1\" else \"0\" end) as risk_flag ,(CASE when (( (select distinct(ogs.person_id) from org_group_students ogs, org_group_faculty ogf, person ip where ogs.org_group_id=ogf.org_group_id and ogf.person_id=($$personId$$) and ogs.person_id=p.id and ogs.person_id = ip.id and ogf.org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg=1 and intent_to_leave= 1 and deleted_at is null) order by ip.risk_level desc ,ip.lastname,ip.firstname) = p.id) or ((select distinct(ocs.person_id) from org_course_student ocs, org_course_faculty ocf, person ip where ocs.org_courses_id=ocf.org_courses_id and ocf.person_id=($$personId$$) and ocs.person_id=p.id and ocs.person_id = ip.id and ocf.org_permissionset_id in (select id from org_permissionset where accesslevel_ind_agg=1 and intent_to_leave= 1 and deleted_at is null) and ocf.org_courses_id in (select id from org_courses where deleted_at is null and org_academic_terms_id in (select id from org_academic_terms where deleted_at is null and end_date > now())) order by ip.risk_level desc ,ip.lastname,ip.firstname )= p.id)) then \"1\" else \"0\" end) as intent_flag from person p join risk_level rl on (p.risk_level = rl.id) left join intent_to_leave il on (p.intent_to_leave = il.id) left join org_person_student as ps on p.id=ps.person_id left outer join Logins_count lc on (lc.person_id = p.id) where (p.id in ( select distinct person_id from org_group_students ogs where ogs.org_group_id in (select org_group_id from org_group_faculty where person_id = ($$personId$$) and deleted_at is null and org_permissionset_id in(select id from org_permissionset op where accesslevel_ind_agg = 1 and deleted_at is null) ) and ogs.deleted_at is null UNION select distinct person_id from org_course_student ocs where ocs.org_courses_id in (select org_courses_id from org_course_faculty where person_id = ($$personId$$) and deleted_at is null and org_courses_id in (select id from org_courses where deleted_at is null and org_academic_terms_id in (select id from org_academic_terms where deleted_at is null and end_date > now()))) and ocs.deleted_at is null ) ) and p.last_contact_date < p.risk_update_date and p.risk_level in ($$risklevel$$) and p.deleted_at is null order by p.risk_level desc ,p.lastname,p.firstname;');
