INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (23,NULL,NULL,NULL,NULL,NULL,NULL,'Activity_All','E',1,'SELECT A.id AS AppointmentId, N.id AS NoteId, R.id AS ReferralId, C.id AS ContactId, AL.id AS activity_log_id, AL.created_at AS activity_date, AL.activity_type AS activity_type, AL.person_id_faculty AS activity_created_by_id, P.firstname AS activity_created_by_first_name, P.lastname AS activity_created_by_last_name, AC.id AS activity_reason_id, AC.short_name AS activity_reason_text, C.contact_types_id AS activity_contact_type_id, CTL.description AS activity_contact_type_text, R.status AS activity_referral_status, C.note AS contactDescription, R.note AS referralDescription, A.description AS appointmentDescription, N.note AS noteDescription FROM activity_log AS AL LEFT JOIN Appointments AS A ON AL.appointments_id = A.id LEFT JOIN note AS N ON AL.note_id = N.id LEFT JOIN note_teams AS NT ON N.id = NT.note_id LEFT JOIN contacts AS C ON AL.contacts_id = C.id LEFT JOIN contacts_teams AS CT ON C.id = CT.contacts_id LEFT JOIN referrals AS R ON AL.referrals_id = R.id LEFT JOIN referrals_teams AS RT ON R.id = RT.referrals_id LEFT JOIN activity_category AS AC ON A.activity_category_id = AC.id OR N.activity_category_id = AC.id OR R.activity_category_id = AC.id OR C.activity_category_id = AC.id LEFT JOIN person AS P ON AL.person_id_faculty = P.id LEFT JOIN contact_types_lang AS CTL ON C.contact_types_id = CTL.contact_types_id WHERE AL.person_id_student = $$studentId$$ AND AL.organization_id = $$orgId$$ AND AL.activity_type IN ($$acivityArr$$) AND AL.deleted_at IS NULL AND A.deleted_at IS NULL AND N.deleted_at IS NULL AND C.deleted_at IS NULL AND R.deleted_at IS NULL AND AL.id NOT IN (SELECT ALOG.id FROM related_activities AS related LEFT JOIN activity_log AS ALOG ON related.note_id = ALOG.note_id WHERE related.note_id IS NOT NULL AND related.deleted_at IS NULL AND ALOG.deleted_at IS NULL) AND AL.id NOT IN (SELECT ALOG.id FROM related_activities AS related LEFT JOIN activity_log AS ALOG ON related.contacts_id = ALOG.contacts_id WHERE related.contacts_id IS NOT NULL AND related.deleted_at IS NULL AND ALOG.deleted_at IS NULL) AND CASE WHEN AL.activity_type = \"N\" THEN CASE WHEN N.access_team = 1 THEN NT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$faculty$$ AND teams_id IN (SELECT teams_id FROM note_teams WHERE note_id = N.id AND deleted_at IS NULL)) AND $$noteTeamAccess$$ = 1 ELSE CASE WHEN N.access_private = 1 THEN N.person_id_faculty = $$faculty$$ ELSE N.access_public = 1 AND $$notePublicAccess$$ = 1 END END ELSE CASE WHEN AL.activity_type = \"C\" THEN CASE WHEN C.access_team = 1 THEN CT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$faculty$$ AND teams_id IN (SELECT teams_id FROM contacts_teams WHERE contacts_id = C.id AND deleted_at IS NULL)) AND $$contactTeamAccess$$ = 1 ELSE CASE WHEN C.access_private = 1 THEN C.person_id_faculty = $$faculty$$ ELSE C.access_public = 1 AND $$contactPublicAccess$$ = 1 END END ELSE CASE WHEN AL.activity_type = \"R\" THEN CASE WHEN R.access_team = 1 THEN RT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$faculty$$ AND teams_id IN (SELECT teams_id FROM referrals_teams WHERE referrals_id = R.id AND deleted_at IS NULL)) AND $$referralTeamAccess$$ = 1 ELSE CASE WHEN R.access_private = 1 THEN R.person_id_faculty = $$faculty$$ ELSE R.access_public = 1 AND $$referralPublicAccess$$ = 1 END END ELSE CASE WHEN AL.activity_type = \"A\" THEN 1 = 1 ELSE 1 =1 END END END END GROUP BY AL.id ORDER BY AL.created_at DESC');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (24,NULL,NULL,NULL,NULL,NULL,NULL,'Activity_Note','E',1,'SELECT N.id AS activity_id, AL.id AS activity_log_id, N.created_at AS activity_date, N.person_id_faculty AS activity_created_by_id, P.firstname AS activity_created_by_first_name, P.lastname AS activity_created_by_last_name, AC.id AS activity_reason_id, AC.short_name AS activity_reason_text, N.note AS activity_description FROM activity_log AS AL LEFT JOIN note AS N ON AL.note_id = N.id LEFT JOIN person AS P ON N.person_id_faculty = P.id LEFT JOIN activity_category AS AC ON N.activity_category_id = AC.id LEFT JOIN note_teams AS NT ON N.id = NT.note_id WHERE AL.person_id_student = $$studentId$$ /*Student id in request parameter */ AND AL.deleted_at IS NULL AND N.deleted_at IS NULL AND CASE WHEN N.access_team = 1 THEN NT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$faculty$$ AND teams_id IN (SELECT teams_id from note_teams WHERE note_id = N.id AND deleted_at IS NULL))AND $$teamAccess$$ = 1 /* logged in person id*/ ELSE CASE WHEN N.access_private = 1 THEN N.person_id_faculty = $$faculty$$ /* logged in person id*/ ELSE N.access_public = 1 AND $$publicAccess$$ = 1 END END GROUP BY N.id');
