INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (58,NULL,NULL,NULL,NULL,NULL,NULL,'Interaction_Activity','P',1,'SELECT C.person_id_faculty AS faculty, C.person_id_student AS student, P.firstname, P.lastname, P.risk_level, RL.risk_text, RL.image_name, IL.text AS intent_to_leave_text, IL.image_name AS intent_to_leave_image, RML.risk_model_id, P.last_activity, OPS.surveycohort AS student_cohort, LC.cnt, OPS.status, OPS.photo_url FROM activity_log AS AL LEFT JOIN contacts AS C ON AL.contacts_id = C.id LEFT JOIN person AS P ON C.person_id_student = P.id LEFT JOIN contact_types_lang AS CTL ON C.contact_types_id = CTL.contact_types_id LEFT JOIN activity_category AS AC ON C.activity_category_id = AC.id LEFT JOIN contacts_teams AS CT ON C.id = CT.contacts_id LEFT JOIN contact_types AS CONT ON C.contact_types_id = CONT.id LEFT JOIN risk_level AS RL ON P.risk_level = RL.id LEFT JOIN risk_model_levels AS RML ON RML.risk_level = RL.id LEFT JOIN intent_to_leave AS IL ON P.intent_to_leave = IL.id LEFT JOIN org_person_student AS OPS ON OPS.person_id = C.person_id_student LEFT JOIN org_course_student AS OCS ON OCS.person_id = C.person_id_student LEFT JOIN org_courses AS OC ON (OCS.org_courses_id = OC.id) LEFT JOIN Logins_count LC ON (LC.person_id = C.person_id_student) WHERE (CONT.parent_contact_types_id = 1 OR CONT.id = 1) AND C.deleted_at IS NULL AND OC.org_academic_year_id = $$yearId$$ AND C.person_id_student IN ($$personIds$$) AND AL.id NOT IN (SELECT ALOG.id FROM related_activities AS related LEFT JOIN activity_log AS ALOG ON related.contacts_id = ALOG.contacts_id WHERE related.contacts_id IS NOT NULL AND related.deleted_at IS NULL AND ALOG.deleted_at IS NULL) AND CASE WHEN C.access_team = 1 THEN CT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$facultyId$$) ELSE CASE WHEN C.access_private = 1 THEN C.person_id_faculty = $$facultyId$$  ELSE C.access_public = 1 END END GROUP BY C.id');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (59,NULL,NULL,NULL,NULL,NULL,NULL,'Non-interaction_Activity','P',1,'SELECT C.person_id_faculty AS faculty, C.person_id_student AS student, P.firstname, P.lastname, P.risk_level, RL.risk_text, RL.image_name, IL.text AS intent_to_leave_text, IL.image_name AS intent_to_leave_image, RML.risk_model_id, P.last_activity, OPS.surveycohort AS student_cohort, LC.cnt, OPS.status, OPS.photo_url FROM activity_log AS AL LEFT JOIN contacts AS C ON AL.contacts_id = C.id LEFT JOIN person AS P ON C.person_id_student = P.id LEFT JOIN contact_types_lang AS CTL ON C.contact_types_id = CTL.contact_types_id LEFT JOIN activity_category AS AC ON C.activity_category_id = AC.id LEFT JOIN contacts_teams AS CT ON C.id = CT.contacts_id LEFT JOIN contact_types AS CONT ON C.contact_types_id = CONT.id LEFT JOIN risk_level AS RL ON P.risk_level = RL.id LEFT JOIN risk_model_levels AS RML ON RML.risk_level = RL.id LEFT JOIN intent_to_leave AS IL ON P.intent_to_leave = IL.id LEFT JOIN org_person_student AS OPS ON OPS.person_id = C.person_id_student LEFT JOIN org_course_student AS OCS ON OCS.person_id = C.person_id_student LEFT JOIN org_courses AS OC ON (OCS.org_courses_id = OC.id) LEFT JOIN Logins_count LC ON (LC.person_id = C.person_id_student) WHERE (CONT.parent_contact_types_id = 2 OR CONT.id = 2) AND C.deleted_at IS NULL AND OC.org_academic_year_id = $$yearId$$ AND C.person_id_student IN ($$personIds$$) AND AL.id NOT IN (SELECT ALOG.id FROM related_activities AS related LEFT JOIN activity_log AS ALOG ON related.contacts_id = ALOG.contacts_id WHERE related.contacts_id IS NOT NULL AND related.deleted_at IS NULL AND ALOG.deleted_at IS NULL) AND CASE WHEN C.access_team = 1 THEN CT.teams_id IN (SELECT teams_id FROM team_members WHERE person_id = $$facultyId$$) ELSE CASE WHEN C.access_private = 1 THEN C.person_id_faculty = $$facultyId$$ ELSE C.access_public = 1 END END GROUP BY C.id');
INSERT INTO `ebi_search` (`id`,`created_by`,`created_at`,`modified_by`,`modified_at`,`deleted_by`,`deleted_at`,`query_key`,`search_type`,`is_enabled`,`query`) VALUES (60,NULL,NULL,NULL,NULL,NULL,NULL,'Have_Not_Been_Reviewed','P',1,'SELECT P.id AS student, OC.org_academic_year_id, P.firstname, P.lastname, P.risk_level, RL.risk_text, RL.image_name, IL.text AS intent_to_leave_text, IL.image_name AS intent_to_leave_image, RML.risk_model_id, P.last_activity, OPS.surveycohort AS student_cohort, LC.cnt, OPS.status, OPS.photo_url FROM person P LEFT JOIN org_course_student OCS ON OCS.person_id = P.id LEFT JOIN org_courses OC ON OC.id = OCS.org_courses_id LEFT JOIN risk_level AS RL ON P.risk_level = RL.id LEFT JOIN risk_model_levels AS RML ON RML.risk_level = RL.id LEFT JOIN intent_to_leave AS IL ON P.intent_to_leave = IL.id LEFT JOIN org_person_student AS OPS ON OPS.person_id = P.id LEFT JOIN Logins_count LC ON (LC.person_id = P.id) LEFT JOIN student_db_view_log SDV ON (SDV.person_id_student = P.id) WHERE OC.deleted_At IS NULL AND OCS.deleted_at IS NULL AND OC.org_academic_year_id = $$yearId$$ AND P.id IN ($$personIds$$ ) AND SDV.organization_id = $$orgId$$ AND SDV.person_id_faculty = $$facultyIds$$ AND SDV.last_viewed_on > P.risk_update_date group by P.id');
